//import formatName from './formatName'
// const name = 'Wika';
// const element = <h1>Witaj, {name}</h1> //w klamrach można umieścić dowolne wyrażenie JS np. 2 + 2, user.firstName czy formatName(user)

function Clock(props) {
  return (
    <div>
      <h1>Witaj, świecie!</h1>
      <h2>Aktualny czas: {props.date.toLocaleTimeString()}.</h2>
    </div>
  );
}

function tick() {
  ReactDOM.render(
    <Clock date={new Date()} />,
    document.getElementById('root')
  );
}

setInterval(tick, 1000);


